import React, { useState } from "react";
import axios from "axios";
import "./Weather.css";

export default function Weather(){
    const [ready, setReady] = useState(false);
    const [weatherData, setWeatherData] = useState({});
    function handleResponse(response){
        console.log(response.data);
        setWeatherData({
            temperature: response.data.main.temp,
            wind: response.data.main.wind,
            city: response.data.main.name,
            humidiy: response.data.main.humidiy,
            precipitation: response.data.main.rain,

        });

        setReady(true);

    }
    if (ready) {
        return <div className="Weather">
        <form>
            <div className="row">
                <div className="col-9">
                    <input type="search" placeholder="search for a city..."className="form-control" autoFocus="on"/>
                </div>
                <div className="col-3">
                <input type="submit" value="Search" className="btn btn-primary w-200"/>   
                </div>                
            </div>
        </form>
        <h1>{weatherData.city}</h1>
        <ul>
            <li> Wednesday 07:00</li>
            <li>{weatherData.description}</li>

        </ul>
        <div className= "row mt-3">
            <div className="col-6">  
                <div className="clearfix">

                <img src="https://ssl.gstatic.com/onebox/weather/48/partly_cloudy.png"
                alt="Mostly Cloudy"
                className="float-left"
                /> 
                <div className="float-left">
                <span className= "temperature">{Math.round(weatherData.temperature)}</span>  
                <span className="unit">Â°C</span>
                </div>
                </div>
            </div>
            <div className="col-6">
                <ul>
                    <li>Precipitation:{weatherData.rain}%</li>
                    <li>Humidity:{weatherData.humidiy}%</li>
                    <li>Wind:{weatherData.wind}km/h</li>
                </ul>
            </div>
        </div>



    </div>;

} else {

    const apiKey = "87a4d33f9276aba60312862a75e60dce";
    let city ="New York";
    let apiUrl = `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${apiKey}&units=metric`;
    axios.get(apiUrl).then(handleResponse);

    return "Loading ...";

    }
}
