import React, {useState} from "react";
import WeatherIcon from "./WeatherIcon";
import axios from "axios";
import "./WeatherForecast.css";


export default function WeatherForecast(props){
const [loaded, setLoaded] = useState(false);
const [forecast, setForecast] = useState(null);


    function handleForscastResponse(response){

        setForecast(response.data);
        setLoaded(true);



    }

    if (loaded) {

        return(
            <div className= "WeatherForecast">

                <WeatherIcon code={forecast.list[0].weather[0].icon} />
                {Math.round(forecast.list[0].main.temp)}°C
                {Math.round(forecast.list[1].main.temp)}°C
                {Math.round(forecast.list[2].main.temp)}°C
                {Math.round(forecast.list[3].main.temp)}°C
                {Math.round(forecast.list[4].main.temp)}°C

            </div> 
        );   
    } else {
      let apiKey = "87a4d33f9276aba60312862a75e60dce";
      let url = `https://api.openweathermap.org/data/2.5/forecast?q=${props.city}&appid=${apiKey}&units=metric`;
        axios.get(url).then(handleForscastResponse);
        return props.city;

    }


}
